import {Form , Button} from 'react-bootstrap'
import './CreatePost.css'

function CreatePost () {

    const [toAddTask,setToAddTask]=useState("")
  
    const handlechange = (e) => {
      setToAddTask(e.target.value)
    }
    // console.log(toAddTask);
    const dispatch=useDispatch()
  
    const handlesubmit =()=>{
      if(toAddTask){
  
        dispatch(addTask({toAddTask:toAddTask}))
        setToAddTask=("")
      }
      else 
            alert("empty Task");
    }
    return(


        <div>
            <br/>
            <br/>
            <br/>
        <div  className="AddPost">
        <h1>CreatePost</h1>
        <div className="AddField">
        <Form.Control  id="AddPostInput" size="lg" type="text" placeholder="Enter Your Post" />
         <Button variant="outline-primary" className="add-todo"   style={{ border:"2px white solid" , color:"white" }} >
            Add</Button>
              
    </div>
    </div>
    </div>
    );
 }   
    export default CreatePost;
